import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { motion } from 'framer-motion';

function ContactList({ newContact }) {
  const [contacts, setContacts] = useState([]);
  const [query, setQuery] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [editedName, setEditedName] = useState('');
  const [editedPhone, setEditedPhone] = useState('');
  const [errorMsg, setErrorMsg] = useState('');

useEffect(() => {
  const loadContacts = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/contacts?q=${query}`);
      setContacts(res.data);
    } catch (err) {
      setErrorMsg('Failed to load contacts.');
    }
  };
  loadContacts();
}, [newContact, query]);


  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/contacts/${id}`);
      loadContacts();
    } catch (err) {
      setErrorMsg('Failed to delete contact.');
    }
  };

  const startEdit = (contact) => {
    setEditingId(contact.id);
    setEditedName(contact.name);
    setEditedPhone(contact.phone);
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditedName('');
    setEditedPhone('');
    setErrorMsg('');
  };

  const handleUpdate = async () => {
    try {
      if (editedName.trim() === '' || editedPhone.trim() === '') {
        setErrorMsg('Name and Phone cannot be empty');
        return;
      }

      await axios.put(`http://localhost:5000/api/contacts/${editingId}`, {
        name: editedName,
        phone: editedPhone,
      });

      cancelEdit();
      loadContacts();
    } catch (err) {
      setErrorMsg('Failed to update contact.');
    }
  };

  return (
    <motion.div
      className="mt-5"
      id="saved"
      initial={{ x: 100, opacity: 0 }}
      animate={{ x: 0, opacity: 1 }}
    >
      <h4 className="mb-3">Saved Contacts</h4>

      <input
        className="form-control mb-3"
        placeholder="Search by name..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />

      {errorMsg && (
        <div className="text-danger mb-2">{errorMsg}</div>
      )}

      <ul className="list-group">
        {contacts.map((c, i) => (
          <motion.li
            key={c.id}
            className="list-group-item d-flex justify-content-between align-items-center"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: i * 0.1 }}
          >
            {editingId === c.id ? (
              <div className="w-100 d-flex flex-column flex-md-row align-items-md-center gap-2">
                <input
                  className="form-control"
                  value={editedName}
                  onChange={(e) => setEditedName(e.target.value)}
                  placeholder="Name"
                />
                <input
                  className="form-control"
                  value={editedPhone}
                  onChange={(e) => setEditedPhone(e.target.value)}
                  placeholder="Phone"
                />
                <div className="d-flex gap-2">
                  <button className="btn btn-success" onClick={handleUpdate}>
                    Save
                  </button>
                  <button className="btn btn-secondary" onClick={cancelEdit}>
                    Cancel
                  </button>
                </div>
              </div>
            ) : (
              <>
                <div>
                  <strong>{c.name}</strong> â€” {c.phone}
                </div>
                <div className="d-flex gap-2">
                  <button className="btn btn-sm btn-warning" onClick={() => startEdit(c)}>
                    Edit
                  </button>
                  <button className="btn btn-sm btn-danger" onClick={() => handleDelete(c.id)}>
                    Delete
                  </button>
                </div>
              </>
            )}
          </motion.li>
        ))}
      </ul>
    </motion.div>
  );
}

export default ContactList;
