import React from 'react';
import { motion } from 'framer-motion';

const listVariants = {
  hidden: { opacity: 0, x: 50 },
  visible: {
    opacity: 1,
    x: 0,
    transition: {
      duration: 0.6,
      when: 'beforeChildren',
      staggerChildren: 0.1,
    },
  },
};

const itemVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: { opacity: 1, y: 0 },
};

function ContactList({ contacts }) {
  return (
    <motion.div
      className="p-3 border rounded bg-white shadow-sm contact-list-glass"
      variants={listVariants}
      initial="hidden"
      animate="visible"
    >
      <h4 className="mb-3">ðŸ“‡ Saved Contacts</h4>
      <ul className="list-group">
        {contacts.map((c, i) => (
          <motion.li
            key={i}
            variants={itemVariants}
            className="list-group-item d-flex justify-content-between align-items-center contact-item"
          >
            <span>ðŸ‘¤ {c.name}</span>
            <span className="text-muted">ðŸ“ž {c.phone}</span>
          </motion.li>
        ))}
      </ul>
    </motion.div>
  );
}

export default ContactList;
