import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { motion } from 'framer-motion';

function ContactList({ newContact }) {
  const [contacts, setContacts] = useState([]);
  const [query, setQuery] = useState('');

  const loadContacts = async () => {
    const res = await axios.get(`http://localhost:5000/api/contacts?q=${query}`);
    setContacts(res.data);
  };

  useEffect(() => {
    loadContacts();
  }, [newContact, query]);

  return (
    <motion.div className="mt-5" id="saved" initial={{ x: 100, opacity: 0 }} animate={{ x: 0, opacity: 1 }}>
      <h4 className="mb-3">Saved Contacts</h4>
      <input className="form-control mb-3" placeholder="Search by name..." value={query} onChange={e => setQuery(e.target.value)} />
      <ul className="list-group">
        {contacts.map((c, i) => (
          <motion.li key={i} className="list-group-item d-flex justify-content-between" initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ delay: i * 0.1 }}>
            <span>{c.name}</span>
            <span>{c.phone}</span>
          </motion.li>
        ))}
      </ul>
    </motion.div>
  );
}

export default ContactList;
